def run(directions, floor=0):
    for direction in directions:
        step = 1 if direction == '(' else -1
        floor += step
    return floor

def position_basement(directions):
    floor = 0
    position = 1
    for direction in directions:
        floor = run(direction,floor)
        if floor >= 0:
            position += 1
        else:
            return position

